function translateObj(obj, lang) {
  if (typeof obj === "string") {
    const t = T[obj] || T[obj.replace(/\u2019/g, "'")]; // normalize curly apostrophe
    return t && t[lang] ? t[lang] : obj;
  }
  if (Array.isArray(obj)) return obj.map((v) => translateObj(v, lang));
  if (obj !== null && typeof obj === "object") {
    const out = {};
    for (const k of Object.keys(obj)) out[k] = translateObj(obj[k], lang);
    return out;
  }
  return obj;
}

// Merge so output has exact same keys as en; use esExisting value when path exists
function mergeWithEnStructure(enObj, otherObj) {
  if (typeof enObj !== "object" || enObj === null) {
    return otherObj !== undefined && typeof otherObj === typeof enObj ? otherObj : enObj;
  }
  const out = {};
  for (const k of Object.keys(enObj)) {
    if (enObj[k] !== null && typeof enObj[k] === "object" && !Array.isArray(enObj[k])) {
      out[k] = mergeWithEnStructure(enObj[k], otherObj && otherObj[k]);
    } else {
      out[k] = otherObj && otherObj[k] !== undefined ? otherObj[k] : enObj[k];
    }
  }
  return out;
}

const es = mergeWithEnStructure(en, esExisting);
